DROP TABLE IF EXISTS `diagnosis`;
CREATE TABLE `diagnosis` (
  `name` varchar(255) DEFAULT NULL,
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `is_delete` varchar(1) DEFAULT NULL COMMENT '1 已删除 0 未删除',
  `parent_id` int(11) DEFAULT NULL,
